// src/components/MapProspects.jsx
import React, { useEffect, useRef, useState } from 'react';
import './MapProspects.css';

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

export default function MapProspects({ data = [], center = { lat: 48.8566, lng: 2.3522 } }) {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markersRef = useRef([]);
  const infoWindowRef = useRef(null);
  const [filteredData, setFilteredData] = useState(data);
  const [filters, setFilters] = useState({
    radius: 100,
    sector: '',
    hasEmail: false,
    hasPhone: false
  });
  const [favorites, setFavorites] = useState(() => {
    return new Set(JSON.parse(localStorage.getItem('favorites') || '[]'));
  });

  // Charge Google Maps API
  useEffect(() => {
    if (!window.google) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_MAPS_API_KEY}`;
      script.async = true;
      script.defer = true;
      script.onload = initMap;
      document.head.appendChild(script);
    } else {
      initMap();
    }
  }, []);

  // Initialise la carte
  const initMap = () => {
    if (!mapRef.current) return;

    mapInstance.current = new window.google.maps.Map(mapRef.current, {
      center: center,
      zoom: 10,
      mapTypeControl: true,
      streetViewControl: false,
      fullscreenControl: true,
      styles: [
        {
          featureType: 'poi',
          elementType: 'labels',
          stylers: [{ visibility: 'off' }]
        }
      ]
    });

    infoWindowRef.current = new window.google.maps.InfoWindow();
  };

  // Cr√©e les markers
  useEffect(() => {
    if (!mapInstance.current || !window.google) return;

    // Nettoie anciens markers
    markersRef.current.forEach(m => m.setMap(null));
    markersRef.current = [];

    const bounds = new window.google.maps.LatLngBounds();
    const seenAddresses = new Map();

    filteredData.forEach(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng || isNaN(lat) || isNaN(lng)) return;

      // D√©duplication
      const addressKey = `${lat.toFixed(4)},${lng.toFixed(4)}`;
      if (seenAddresses.has(addressKey)) return;
      seenAddresses.set(addressKey, true);

      const position = { lat, lng };
      const hasContacts = company.contacts && company.contacts.length > 0;
      const isFavorite = favorites.has(company.siren);

      const marker = new window.google.maps.Marker({
        position,
        map: mapInstance.current,
        title: company.nom,
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: isFavorite ? 12 : 8,
          fillColor: isFavorite ? '#f4b400' : (hasContacts ? '#0f9d58' : '#db4437'),
          fillOpacity: 0.9,
          strokeColor: '#ffffff',
          strokeWeight: 2
        }
      });

      marker.addListener('click', () => {
        showInfoWindow(marker, company);
      });

      markersRef.current.push(marker);
      bounds.extend(position);
    });

    if (markersRef.current.length > 0) {
      mapInstance.current.fitBounds(bounds);
    }
  }, [filteredData, favorites]);

  // InfoWindow
  const showInfoWindow = (marker, company) => {
    const contactsHtml = company.contacts?.length > 0
      ? `
        <div style="margin-top: 12px;">
          <strong>üë• Contacts (${company.contacts.length})</strong>
          ${company.contacts.map(c => `
            <div style="padding: 8px; background: #f8f9fa; border-radius: 6px; margin-top: 6px;">
              <div style="font-weight: 600;">${c.nom || 'Nom inconnu'}</div>
              <div style="font-size: 12px; color: #666;">${c.poste || ''}</div>
              ${c.email ? `<div style="font-size: 12px;">‚úâÔ∏è ${c.email}</div>` : ''}
              ${c.telephone ? `<div style="font-size: 12px;">üìû ${c.telephone}</div>` : ''}
              ${c.linkedin ? `<div style="font-size: 12px;"><a href="${c.linkedin}" target="_blank">üîó LinkedIn</a></div>` : ''}
            </div>
          `).join('')}
        </div>
      `
      : '<div style="color: #999; margin-top: 12px;">Aucun contact disponible</div>';

    const content = `
      <div style="max-width: 350px; padding: 12px;">
        <h3 style="margin: 0 0 12px 0; padding-bottom: 8px; border-bottom: 2px solid #1a73e8;">
          ${company.nom || 'Entreprise'}
        </h3>
        <p style="margin: 8px 0;">
          <strong>üìç Adresse</strong><br>
          ${company.adresse || ''}<br>
          ${company.code_postal || ''} ${company.ville || ''}
        </p>
        <p style="margin: 8px 0;">
          <strong>üè¢ Secteur</strong><br>
          ${company.secteur || 'Non renseign√©'}<br>
          <span style="color: #999; font-size: 12px;">NAF: ${company.naf || 'N/A'}</span>
        </p>
        ${company.site_web ? `
          <p style="margin: 8px 0;">
            <strong>üåê Site web</strong><br>
            <a href="${company.site_web}" target="_blank">${company.site_web}</a>
          </p>
        ` : ''}
        ${company.telephone ? `
          <p style="margin: 8px 0;">
            <strong>üìû T√©l√©phone</strong><br>
            <a href="tel:${company.telephone}">${company.telephone}</a>
          </p>
        ` : ''}
        ${contactsHtml}
      </div>
    `;

    infoWindowRef.current.setContent(content);
    infoWindowRef.current.open(mapInstance.current, marker);
  };

  // Applique les filtres
  const applyFilters = () => {
    let filtered = data.filter(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng) return false;

      // Filtre rayon
      const distance = calculateDistance(center.lat, center.lng, lat, lng);
      if (distance > filters.radius) return false;

      // Filtre secteur
      if (filters.sector && company.naf !== filters.sector) return false;

      // Filtre email
      if (filters.hasEmail && (!company.contacts || !company.contacts.some(c => c.email))) {
        return false;
      }

      // Filtre t√©l√©phone
      if (filters.hasPhone && !company.telephone && (!company.contacts || !company.contacts.some(c => c.telephone))) {
        return false;
      }

      return true;
    });

    setFilteredData(filtered);
  };

  useEffect(() => {
    applyFilters();
  }, [filters, data]);

  // Calcul distance
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Toggle favoris
  const toggleFavorite = (siren) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(siren)) {
      newFavorites.delete(siren);
    } else {
      newFavorites.add(siren);
    }
    setFavorites(newFavorites);
    localStorage.setItem('favorites', JSON.stringify([...newFavorites]));
  };

  const sectors = [...new Set(data.map(c => c.naf).filter(Boolean))];

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{ width: '380px', background: 'white', borderRight: '1px solid #ddd', overflowY: 'auto', padding: '20px' }}>
        <h2>üéØ Prospection Interactive</h2>
        <div style={{ marginBottom: '20px' }}>
          <span style={{ background: '#e8f0fe', padding: '6px 12px', borderRadius: '16px', marginRight: '8px' }}>
            {data.length} entreprises
          </span>
          <span style={{ background: '#e8f0fe', padding: '6px 12px', borderRadius: '16px' }}>
            {filteredData.length} visibles
          </span>
        </div>

        {/* Filtres */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>
            üìç Rayon: {filters.radius} km
          </label>
          <input
            type="range"
            min="0"
            max="100"
            step="5"
            value={filters.radius}
            onChange={(e) => setFilters({ ...filters, radius: parseInt(e.target.value) })}
            style={{ width: '100%' }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>üè¢ Secteur NAF</label>
          <select
            value={filters.sector}
            onChange={(e) => setFilters({ ...filters, sector: e.target.value })}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }}
          >
            <option value="">Tous les secteurs</option>
            {sectors.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
            <input
              type="checkbox"
              checked={filters.hasEmail}
              onChange={(e) => setFilters({ ...filters, hasEmail: e.target.checked })}
              style={{ marginRight: '8px' }}
            />
            Avec Email
          </label>
          <label style={{ display: 'flex', alignItems: 'center' }}>
            <input
              type="checkbox"
              checked={filters.hasPhone}
              onChange={(e) => setFilters({ ...filters, hasPhone: e.target.checked })}
              style={{ marginRight: '8px' }}
            />
            Avec T√©l√©phone
          </label>
        </div>

        {/* Liste */}
        {filteredData.map((company, i) => {
          const isFav = favorites.has(company.siren);
          return (
            <div
              key={i}
              style={{
                background: 'white',
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '16px',
                marginBottom: '12px',
                cursor: 'pointer'
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                <strong>{company.nom}</strong>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleFavorite(company.siren);
                  }}
                  style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '20px' }}
                >
                  {isFav ? '‚≠ê' : '‚òÜ'}
                </button>
              </div>
              <div style={{ fontSize: '13px', color: '#666' }}>
                üìç {company.ville}<br />
                üè¢ {company.secteur?.substring(0, 50)}
              </div>
            </div>
          );
        })}
      </div>

      {/* Carte */}
      <div ref={mapRef} style={{ flex: 1 }} />
    </div>
  );
}
