import React, { useState } from "react";
import CompanyProspectList from "./companyprospect";

export default function App() {
  const [companyName, setCompanyName] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSearch() {
  if (!companyName || !postalCode) {
    alert("Veuillez entrer le nom et le code postal de l'entreprise.");
    return;
  }

  setLoading(true);
  setError(null);

  try {
    // === MOCK : données d'exemple ===
    const mockData = [
      {
        nom: "Entreprise Exemple 1",
        adresse: "123 Rue Test, 75000 Paris",
        secteur: "BTP",
        resume: "Entreprise spécialisée dans la construction et rénovation de bâtiments.",
        site_web: "https://exemple1.com",
        contacts: [
          { poste: "DG", nom: "Jean Dupont", email: "j.dupont@exemple1.com", telephone: "0102030405", linkedin: "" },
          { poste: "RSE", nom: "Marie Martin", email: "m.martin@exemple1.com", telephone: "0102030406", linkedin: "" },
          { poste: "Supply Chain", nom: "Luc Durand", email: "l.durand@exemple1.com", telephone: "0102030407", linkedin: "" }
        ],
        sources: ["Mock data"]
      },
      {
        nom: "Entreprise Exemple 2",
        adresse: "456 Avenue Test, 75001 Paris",
        secteur: "BTP",
        resume: "Entreprise active dans la construction de logements et bâtiments publics.",
        site_web: "https://exemple2.com",
        contacts: [
          { poste: "DG", nom: "Claire Petit", email: "c.petit@exemple2.com", telephone: "0102030410", linkedin: "" },
          { poste: "RSE", nom: "Paul Leroy", email: "p.leroy@exemple2.com", telephone: "0102030411", linkedin: "" }
        ],
        sources: ["Mock data"]
      }
    ];

    // Simule un délai comme si c'était une requête réseau
    await new Promise((resolve) => setTimeout(resolve, 500));

    setData(mockData);
  } catch (err) {
    console.error(err);
    setError("Impossible de récupérer les données. Vérifiez le backend.");
  } finally {
    setLoading(false);
  }
}


  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Prospection Entreprises</h1>

      <div className="flex gap-2 mb-4">
        <input
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          placeholder="Nom de l'entreprise"
          className="flex-1 p-2 border rounded"
        />
        <input
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          placeholder="Code postal"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={handleSearch}
          className="px-3 py-2 bg-slate-700 text-white rounded"
        >
          Rechercher
        </button>
      </div>

      {loading && <p>Recherche en cours...</p>}
      {error && <p className="text-red-600">{error}</p>}

      {data.length > 0 && <CompanyProspectList data={data} />}
    </div>
  );
}
