import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './MapProspect.css';

// Fix des icÃ´nes Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

export default function MapProspects({ onSearch }) {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markersRef = useRef([]);
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [companyName, setCompanyName] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [center, setCenter] = useState({ lat: 48.8566, lng: 2.3522 });
  const [filters, setFilters] = useState({
    radius: 100,
    hasEmail: false,
    hasPhone: false
  });
  const [favorites, setFavorites] = useState(() => {
    return new Set(JSON.parse(localStorage.getItem('favorites') || '[]'));
  });
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Calcul distance (Haversine)
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const geocodePostal = async (cp) => {
    try {
      const res = await fetch(
        `https://api-adresse.data.gouv.fr/search/?q=${encodeURIComponent(cp)}&limit=1`
      );
      const json = await res.json();
      const feat = json.features?.[0];
      if (!feat) return null;
      const [lon, lat] = feat.geometry.coordinates;
      return { lat, lng: lon };
    } catch (error) {
      console.error('Erreur de gÃ©ocodage:', error);
      return null;
    }
  };

  // FONCTION DE RECHERCHE UNIFIÃ‰E
  const handleSearch = async (e) => {
    if (e) e.preventDefault();

    if (!companyName || !postalCode) {
      setError('Nom et code postal requis');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // 1. GÃ‰OCODAGE D'ABORD pour avoir un centre prÃ©cis
      const centerFromCp = await geocodePostal(postalCode);
      if (!centerFromCp) {
        setError('Code postal introuvable');
        setLoading(false);
        return;
      }

      setCenter(centerFromCp);

      // 2. Appel backend
      const res = await fetch('http://localhost:5000/prospect', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          companyName,
          postalCode,
          radiusKm: filters.radius || 100,
        }),
      });

      const text = await res.text();
      let js = null;
      try {
        js = text ? JSON.parse(text) : null;
      } catch (parseErr) {
        setError(`RÃ©ponse inattendue du serveur (status ${res.status})`);
        setLoading(false);
        return;
      }

      if (!res.ok) {
        setError(js?.error || `Erreur serveur (${res.status})`);
        setLoading(false);
        return;
      }

      // 3. Mapping des entreprises
      const companies = (js.data || []).map((c, i) => ({
        id: i,
        name: c.nom || c.nom_entreprise || "Inconnu",
        address: c.adresse || c.adresse_siege || "",
        sector: c.secteur || c.libelle_activite_principale || c.libelle_naf || "",
        site: c.site_web || c.site || "",
        telephone: c.telephone || "",
        contacts: c.contacts || [],
        latitude: c.latitude || c.lat || null,
        longitude: c.longitude || c.lng || null,
        ville: c.ville || "",
        code_postal: c.code_postal || "",
        adresse: c.adresse || "",
        naf: c.naf || c.activite_principale || "",
        siren: c.siren || `temp-${i}`,
      }));

      setData(companies);

      // 4. FILTRAGE UNIFIÃ‰ avec toutes les conditions
      const filtered = companies.filter(company => {
        // VÃ©rification des coordonnÃ©es
        const lat = parseFloat(company.latitude);
        const lng = parseFloat(company.longitude);
        if (!lat || !lng || isNaN(lat) || isNaN(lng)) return false;

        // Filtre distance
        const distance = calculateDistance(centerFromCp.lat, centerFromCp.lng, lat, lng);
        if (distance > filters.radius) return false;

        // Filtre email
        if (filters.hasEmail && (!company.contacts || !company.contacts.some(c => c.email))) {
          return false;
        }

        // Filtre tÃ©lÃ©phone
        if (filters.hasPhone && !company.telephone && (!company.contacts || !company.contacts.some(c => c.telephone))) {
          return false;
        }

        return true;
      });

      setFilteredData(filtered);

      // 5. Centrage de la carte sur le CP gÃ©ocodÃ©
      if (mapInstance.current) {
        mapInstance.current.setView([centerFromCp.lat, centerFromCp.lng], 11);
      }

    } catch (err) {
      setError(err.message || 'Erreur rÃ©seau');
    } finally {
      setLoading(false);
    }
  };

  // APPLY FILTERS - seulement pour les filtres appliquÃ©s aprÃ¨s la recherche
  const applyFilters = () => {
    if (data.length === 0) {
      setFilteredData([]);
      return;
    }

    const filtered = data.filter(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng) return false;

      // Filtre distance (utilise le centre actuel)
      const distance = calculateDistance(center.lat, center.lng, lat, lng);
      if (distance > filters.radius) return false;

      // Filtre email
      if (filters.hasEmail && (!company.contacts || !company.contacts.some(c => c.email))) {
        return false;
      }

      // Filtre tÃ©lÃ©phone
      if (filters.hasPhone && !company.telephone && (!company.contacts || !company.contacts.some(c => c.telephone))) {
        return false;
      }

      return true;
    });

    setFilteredData(filtered);
  };

  // Applique les filtres quand ils changent
  useEffect(() => {
    if (data.length > 0) {
      applyFilters();
    }
  }, [filters, center]);

  // Initialise la carte Leaflet
  useEffect(() => {
    if (!mapRef.current || mapInstance.current) return;

    mapInstance.current = L.map(mapRef.current).setView([center.lat, center.lng], 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Â© OpenStreetMap contributors',
      maxZoom: 19
    }).addTo(mapInstance.current);

    // Nettoyage
    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, []);

  // CrÃ©e les markers
  useEffect(() => {
    if (!mapInstance.current) return;

    // Nettoie anciens markers
    markersRef.current.forEach(m => mapInstance.current.removeLayer(m));
    markersRef.current = [];

    if (filteredData.length === 0) return;

    const bounds = [];
    const seenAddresses = new Map();

    filteredData.forEach(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng || isNaN(lat) || isNaN(lng)) return;

      // DÃ©duplication
      const addressKey = `${lat.toFixed(4)},${lng.toFixed(4)}`;
      if (seenAddresses.has(addressKey)) return;
      seenAddresses.set(addressKey, true);

      // IcÃ´ne personnalisÃ©e selon la charte graphique
      const hasContacts = company.contacts && company.contacts.length > 0;
      const isFavorite = favorites.has(company.siren);

      // Couleurs selon la charte graphique
      let iconColor, textColor, symbol;

      if (isFavorite) {
        iconColor = '#00FF89'; // Vert pour les favoris
        textColor = '#000000'; // Texte noir sur fond vert
        symbol = 'â˜…';
      } else if (hasContacts) {
        iconColor = '#000000'; // Noir pour les entreprises avec contacts
        textColor = '#00FF89'; // Texte vert sur fond noir
        symbol = 'E';
      } else {
        iconColor = '#000000'; // Noir pour les entreprises sans contacts
        textColor = '#FFFFFF'; // Texte blanc sur fond noir
        symbol = '!';
      }

      const iconSize = isFavorite ? 32 : 24;
      const borderWidth = isFavorite ? 3 : 2;

      // Dans le useEffect des markers, modifiez la crÃ©ation de l'icÃ´ne :
        const customIcon = L.divIcon({
        className: 'custom-leaflet-marker',
        html: `
            <div style="
            background: ${iconColor};
            width: ${iconSize}px;
            height: ${iconSize}px;
            border-radius: 0;
            border: ${borderWidth}px solid #000000;
            display: flex;
            align-items: center;
            justify-content: center;
            font-family: 'DM Mono', monospace;
            font-weight: 500;
            color: ${textColor};
            font-size: ${iconSize * 0.5}px;
            line-height: 1;
            transform: translate(-50%, -50%); /* â† AJOUT IMPORTANT */
            ">
            ${symbol}
            </div>
        `,
        iconSize: [iconSize, iconSize],
        iconAnchor: [iconSize/2, iconSize/2],
        popupAnchor: [0, -iconSize/2] // â† AJOUT pour mieux positionner le popup
        });

      const marker = L.marker([lat, lng], { icon: customIcon })
        .addTo(mapInstance.current)
        .bindPopup(() => createPopupContent(company))
        .on('click', () => setSelectedCompany(company.siren));

      markersRef.current.push(marker);
      bounds.push([lat, lng]);
    });

    // Ajuste le zoom
    if (bounds.length > 0) {
      mapInstance.current.fitBounds(bounds, { padding: [50, 50] });
    }
  }, [filteredData, favorites]);

  // Popup personnalisÃ©e
  const createPopupContent = (company) => {
    const contactsHtml = company.contacts && company.contacts.length > 0
      ? `
        <div style="margin-top: 12px;">
          <strong>ğŸ‘¥ Contacts (${company.contacts.length})</strong>
          ${company.contacts.map(c => `
            <div style="padding: 8px; background: #EAEAF6; border-radius: 6px; margin-top: 6px;">
              <div style="font-weight: 600; font-family: 'DM Mono', monospace;">${c.nom || 'Nom inconnu'}</div>
              <div style="font-size: 12px; color: #666;">${c.poste || ''}</div>
              ${c.email ? `<div style="font-size: 12px;">âœ‰ï¸ ${c.email}</div>` : ''}
              ${c.telephone ? `<div style="font-size: 12px;">ğŸ“ ${c.telephone}</div>` : ''}
              ${c.linkedin ? `<div style="font-size: 12px;"><a href="${c.linkedin}" target="_blank">ğŸ”— LinkedIn</a></div>` : ''}
            </div>
          `).join('')}
        </div>
      `
      : '<div style="color: #999; margin-top: 12px;">Aucun contact disponible</div>';

    return `
      <div style="max-width: 350px; padding: 12px; font-family: 'Inter', sans-serif;">
        <h3 style="margin: 0 0 12px 0; padding-bottom: 8px; border-bottom: 2px solid #000000; font-family: 'DM Mono', monospace; text-transform: uppercase;">
          ${company.name || company.nom || 'Entreprise'}
        </h3>
        <p style="margin: 8px 0;">
          <strong>ğŸ“ Adresse</strong><br>
          ${company.address || company.adresse || ''}<br>
          ${company.code_postal || ''} ${company.ville || ''}
        </p>
        <p style="margin: 8px 0;">
          <strong>ğŸ¢ Secteur</strong><br>
          ${company.sector || company.secteur || 'Non renseignÃ©'}<br>
          <span style="color: #999; font-size: 12px;">NAF: ${company.naf || 'N/A'}</span>
        </p>
        ${company.site || company.site_web ? `
          <p style="margin: 8px 0;">
            <strong>ğŸŒ Site web</strong><br>
            <a href="${company.site || company.site_web}" target="_blank" style="color: #000000; text-decoration: underline;">${company.site || company.site_web}</a>
          </p>
        ` : ''}
        ${company.telephone ? `
          <p style="margin: 8px 0;">
            <strong>ğŸ“ TÃ©lÃ©phone</strong><br>
            <a href="tel:${company.telephone}" style="color: #000000; text-decoration: underline;">${company.telephone}</a>
          </p>
        ` : ''}
        ${contactsHtml}
      </div>
    `;
  };

  // Toggle favoris
    const toggleFavorite = (siren) => {
        const newFavorites = new Set(favorites);
        if (newFavorites.has(siren)) {
        newFavorites.delete(siren);
        } else {
        newFavorites.add(siren);
        }
        setFavorites(newFavorites);
        localStorage.setItem('favorites', JSON.stringify([...newFavorites]));
    };

    // Fonction pour convertir les donnÃ©es en CSV
const convertToCSV = (data) => {
  if (!data || data.length === 0) return '';

  // En-tÃªtes du CSV
  const headers = [
    'Nom',
    'Adresse',
    'Ville',
    'Code Postal', 
    'Secteur',
    'NAF',
    'Site Web',
    'TÃ©lÃ©phone',
    'Email',
    'Contacts',
    'SIREN'
  ];

  // Lignes de donnÃ©es
  const rows = data.map(company => {
    const contacts = company.contacts || [];
    const emails = contacts.filter(c => c.email).map(c => c.email).join('; ');
    const phones = [
      company.telephone,
      ...contacts.filter(c => c.telephone).map(c => c.telephone)
    ].filter(Boolean).join('; ');

    const contactNames = contacts.map(c => 
      `${c.nom || ''}${c.poste ? ` (${c.poste})` : ''}`
    ).join('; ');

    return [
      `"${(company.name || company.nom || '').replace(/"/g, '""')}"`,
      `"${(company.address || company.adresse || '').replace(/"/g, '""')}"`,
      `"${(company.ville || '').replace(/"/g, '""')}"`,
      `"${(company.code_postal || '').replace(/"/g, '""')}"`,
      `"${(company.sector || company.secteur || '').replace(/"/g, '""')}"`,
      `"${(company.naf || '').replace(/"/g, '""')}"`,
      `"${(company.site || company.site_web || '').replace(/"/g, '""')}"`,
      `"${phones}"`,
      `"${emails}"`,
      `"${contactNames}"`,
      `"${company.siren}"`
    ].join(',');
  });

  return [headers.join(','), ...rows].join('\n');
};

// Fonction de tÃ©lÃ©chargement
const downloadCSV = (csvContent, filename) => {
  const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement('a');
  const url = URL.createObjectURL(blob);

  link.setAttribute('href', url);
  link.setAttribute('download', filename);
  link.style.visibility = 'hidden';

  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

// Export des favoris
const exportFavorites = () => {
  if (favorites.size === 0) {
    alert('Aucun favori Ã  exporter');
    return;
  }

  const favoriteCompanies = data.filter(company => 
    favorites.has(company.siren)
  );

  const csv = convertToCSV(favoriteCompanies);
  downloadCSV(csv, `favoris-entreprises-${new Date().toISOString().split('T')[0]}.csv`);
};

// Export de toute la liste
const exportAll = () => {
  if (filteredData.length === 0) {
    alert('Aucune donnÃ©e Ã  exporter');
    return;
  }

  const csv = convertToCSV(filteredData);
  downloadCSV(csv, `entreprises-${new Date().toISOString().split('T')[0]}.csv`);
};

    const focusOnCompany = (company) => {
    if (!company || !mapInstance.current) return;

    const lat = parseFloat(company.latitude);
    const lng = parseFloat(company.longitude);

    if (!lat || !lng || isNaN(lat) || isNaN(lng)) return;

    // DÃ©lai de 0.5 seconde avant l'action
    setTimeout(() => {
        // DÃ©sactive temporairement les animations de zoom problÃ©matiques
        mapInstance.current.options.zoomAnimation = false;

        // Centre d'abord la carte SANS animation
        mapInstance.current.setView([lat, lng], 15);

        // RÃ©active les animations aprÃ¨s un court dÃ©lai
        setTimeout(() => {
        mapInstance.current.options.zoomAnimation = true;

        // Ouvre le popup
        const companyMarker = markersRef.current.find(marker => {
            const markerLatLng = marker.getLatLng();
            const latDiff = Math.abs(markerLatLng.lat - lat);
            const lngDiff = Math.abs(markerLatLng.lng - lng);
            return latDiff < 0.0001 && lngDiff < 0.0001;
        });

        if (companyMarker) {
            // Ferme tous les autres popups d'abord
            markersRef.current.forEach(marker => {
            if (marker !== companyMarker && marker.isPopupOpen()) {
                marker.closePopup();
            }
            });

            // Ouvre le popup aprÃ¨s un petit dÃ©lai supplÃ©mentaire
            setTimeout(() => {
            companyMarker.openPopup();
            }, 100);
        }
        }, 50);
    }, 500); // â† DÃ©lai principal de 500ms (0.5 seconde)
    };

  return (
    <div className="prospect-layout">
      {/* Sidebar principal */}
      <div id="sidebar">
        <div className="sidebar-center">
          {/* Titre de section */}
          <h2 className="section-title">Prospection</h2>

          {/* Formulaire de recherche */}
          <form className="panel-content" onSubmit={handleSearch}>
            <div id="search-container">
              <input
                id="search-input"
                type="text"
                placeholder="Ex: Castorama"
                value={companyName}
                onChange={(e) => setCompanyName(e.target.value)}
              />
            </div>
            <div id="search-container">
              <input
                id="search-input"
                type="text"
                placeholder="Ex: 75015"
                value={postalCode}
                onChange={(e) => setPostalCode(e.target.value)}
              />
            </div>

            {/* Filtre rayon avec slider */}
            <div id="association-distance-filter">
              <div className="capacity-filter-title">Rayon de recherche : {filters.radius} km</div>
              <div className="capacity-filter-controls">
                <input
                  type="range"
                  min="1"
                  max="500"
                  value={filters.radius}
                  onChange={(e) => setFilters({ ...filters, radius: parseInt(e.target.value) })}
                  className="capacity-range"
                  id="distance-slider"
                  style={{ 
                    '--progress': `${(filters.radius / 500) * 100}%`
                  }}
                />
              </div>

              <div className="slider-labels">
                <span>1 km</span>
                <span>500 km</span>
              </div>
            </div>

            <button className="btn btn-primary" type="submit" disabled={loading}>
              {loading ? 'ğŸ”„ Recherche...' : 'Rechercher'}
            </button>
          </form>

          {error && <div className="error-message">{error}</div>}

          {/* Filtres simplifiÃ©s */}
          <div className="filters-section">
            <div className="filter-toggle-group">
              <label className="filter-toggle-label" htmlFor="toggle-email">
                Avec email
              </label>
              <input
                type="checkbox"
                id="toggle-email"
                className="filter-toggle-radio"
                checked={!!filters.hasEmail}
                onChange={e => setFilters({ ...filters, hasEmail: e.target.checked })}
              />
            </div>

            <div className="filter-toggle-group">
              <label className="filter-toggle-label" htmlFor="toggle-phone">
                Avec tÃ©lÃ©phone
              </label>
              <input
                type="checkbox"
                id="toggle-phone"
                className="filter-toggle-radio"
                checked={!!filters.hasPhone}
                onChange={e => setFilters({ ...filters, hasPhone: e.target.checked })}
              />
            </div>
          </div>
          {/* AprÃ¨s la section des filtres, ajoutez : */}
            <div className="export-section">
            <h3 className="export-title">Export des donnÃ©es</h3>
            <div className="export-buttons">
                <button 
                className="btn btn-export"
                onClick={exportAll}
                disabled={filteredData.length === 0}
                >
                ğŸ“¥ Toute la liste ({filteredData.length})
                </button>
                <button 
                className="btn btn-export-favorites"
                onClick={exportFavorites}
                disabled={favorites.size === 0}
                >
                â­ Favoris ({favorites.size})
                </button>
            </div>
            </div>

          {/* Liste des entreprises */}
          <div className="transaction-list">
            {filteredData.length === 0 && !loading && (
              <div className="no-results">Aucune entreprise trouvÃ©e</div>
            )}

            {filteredData
                .sort((a, b) => {
                    const aIsFavorite = favorites.has(a.siren);
                    const bIsFavorite = favorites.has(b.siren);

                    // Si a est favori et b ne l'est pas, a vient en premier
                    if (aIsFavorite && !bIsFavorite) return -1;
                    // Si b est favori et a ne l'est pas, b vient en premier  
                    if (!aIsFavorite && bIsFavorite) return 1;
                    // Sinon, ordre original
                    return 0;
                })
                .map((company, i) => (
              <div
                className={`transaction-card${selectedCompany === company.siren ? " selected" : ""}${favorites.has(company.siren) ? " favori" : ""}`}
                key={i}
                onClick={() => {
                    setSelectedCompany(company.siren);
                    focusOnCompany(company);
                  }}
              >
                <div className="transaction-header">
                  <span className="card-title">{company.name || company.nom}</span>
                  <button
                    className="favorite-btn"
                    type="button"
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleFavorite(company.siren);
                    }}
                  >
                    {favorites.has(company.siren) ? "â˜…" : "â˜†"}
                  </button>
                </div>
                <div className="card-details">
                  <div>ğŸ“ {company.ville}</div>
                  <div>ğŸ¢ {company.sector || company.secteur}</div>
                  {company.contacts?.some(c => c.email) && <div>âœ‰ï¸ Avec email</div>}
                  {(company.telephone || company.contacts?.some(c => c.telephone)) && <div>ğŸ“ Avec tÃ©lÃ©phone</div>}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Carte interactive */}
      <div id="map" ref={mapRef}></div>
    </div>
  );
}
