import React, { useState, useMemo } from "react";

export default function CompanyProspect({ data }) {
  const [query, setQuery] = useState("");
  const [openIdx, setOpenIdx] = useState(null);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return data;
    return data.filter(
      (e) =>
        (e.nom || "").toLowerCase().includes(q) ||
        (e.secteur || "").toLowerCase().includes(q) ||
        (e.resume || "").toLowerCase().includes(q)
    );
  }, [data, query]);

  const toggle = (i) => setOpenIdx(openIdx === i ? null : i);

  const exportCSV = (list) => {
    const header = [
      "nom","adresse","secteur","resume","site_web",
      "contact_poste","contact_nom","contact_email","contact_tel","contact_linkedin",
    ];
    const rows = [];
    list.forEach((ent) => {
      const contacts = ent.contacts && ent.contacts.length ? ent.contacts : [{
        poste: "non disponible", nom: "non disponible",
        email: "non disponible", telephone: "non disponible", linkedin: ""
      }];
      contacts.slice(0,3).forEach(c => {
        rows.push([
          ent.nom || "", ent.adresse || "", ent.secteur || "", (ent.resume || "").replace(/[\r\n]+/g," "),
          ent.site_web || "", c.poste || "", c.nom || "", c.email || "", c.telephone || "", c.linkedin || ""
        ]);
      });
    });
    const csv = [header,...rows].map(r => r.map(v => `"${String(v||"").replace(/"/g,'""')}"`).join(",")).join("\r\n");
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url; a.download = "prospects.csv"; a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="flex flex-col sm:flex-row gap-2 mb-6">
        <input
          value={query} onChange={e => setQuery(e.target.value)}
          placeholder="Rechercher nom / secteur / résumé"
          className="flex-1 p-2 border rounded shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />
        <button
          onClick={() => exportCSV(filtered)}
          className="px-4 py-2 bg-indigo-600 text-white rounded shadow hover:bg-indigo-700"
        >
          Export CSV
        </button>
      </div>

      <div className="space-y-4">
        {filtered.map((ent,i) => (
          <div key={i} className="border rounded-lg shadow-sm p-4 bg-white dark:bg-gray-800 transition-colors">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-lg font-semibold text-gray-800 dark:text-gray-100">{ent.nom}</h2>
                <p className="text-sm text-gray-500 dark:text-gray-300">{ent.secteur} • {ent.adresse}</p>
              </div>
              <div className="flex items-center gap-2">
                {ent.site_web && <a href={ent.site_web} target="_blank" rel="noopener" className="text-sm underline text-indigo-600 dark:text-indigo-400">Site</a>}
                <button onClick={() => toggle(i)} className="px-2 py-1 border rounded text-sm text-gray-700 dark:text-gray-200">
                  {openIdx === i ? "Réduire" : "Détails"}
                </button>
              </div>
            </div>

            {openIdx === i && (
              <div className="mt-4 space-y-3 text-sm text-gray-700 dark:text-gray-200">
                <p>{ent.resume}</p>
                <div className="font-medium mb-1">Contacts</div>
                {ent.contacts && ent.contacts.length ? (
                  <div className="space-y-2">
                    {ent.contacts.slice(0,3).map((c,ci) => (
                      <div key={ci} className="flex flex-col sm:flex-row justify-between gap-2 p-2 bg-gray-50 dark:bg-gray-700 rounded">
                        <div>
                          <div><strong>{c.poste}</strong> — {c.nom}</div>
                          <div className="text-xs text-gray-500 dark:text-gray-300 mt-1">
                            Email: {c.email ? <a href={`mailto:${c.email}`} className="underline text-indigo-600 dark:text-indigo-400">{c.email}</a> : "non disponible"} •
                            Tel: {c.telephone ? <a href={`tel:${c.telephone}`} className="underline text-indigo-600 dark:text-indigo-400">{c.telephone}</a> : "non disponible"}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : <div className="text-gray-500 dark:text-gray-400">Contacts non disponibles</div>}
                <div className="text-xs text-gray-400 dark:text-gray-500">
                  Sources: {ent.sources?.join(", ") || "non disponible"}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
