import React, { useState } from "react";
import CompanyProspectList from "./companyprospect.jsx";

export default function App() {
  const [companyName, setCompanyName] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSearch() {
  if (!companyName || !postalCode) {
    alert("Veuillez entrer le nom et le code postal de l'entreprise.");
    return;
  }

  setLoading(true);
  setError(null);

  try {
    const response = await fetch("http://localhost:5000/prospect", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ companyName, location: postalCode }),
    });

    if (!response.ok) throw new Error("Erreur lors de la recherche");

    const json = await response.json();

    if (json.data) {
      setData(json.data);
    } else {
      setData([]);
      setError(json.error || "Aucune donnée reçue");
    }

  } catch (err) {
    console.error(err);
    setError("Impossible de récupérer les données. Vérifiez le backend.");
  } finally {
    setLoading(false);
  }
}

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Prospection Entreprises</h1>

      <div className="flex gap-2 mb-4">
        <input
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          placeholder="Nom de l'entreprise"
          className="flex-1 p-2 border rounded"
        />
        <input
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          placeholder="Code postal"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={handleSearch}
          className="px-3 py-2 bg-slate-700 text-white rounded"
        >
          {loading ? "Chargement..." : "Rechercher"}
        </button>
      </div>

      {error && <p className="text-red-600">{error}</p>}
      {loading && <p>Recherche en cours...</p>}

      {data.length > 0 && <CompanyProspectList data={data} />}
    </div>
  );
}
