import React, { useState, useMemo } from "react";

console.log("Affichage des données :", data);
export default function CompanyProspectList({ data }) {
  const [query, setQuery] = useState("");
  const [openIdx, setOpenIdx] = useState(null);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return data;
    return data.filter(
      (e) =>
        (e.nom || "").toLowerCase().includes(q) ||
        (e.secteur || "").toLowerCase().includes(q) ||
        (e.resume || "").toLowerCase().includes(q)
    );
  }, [data, query]);

  const toggle = (i) => setOpenIdx(openIdx === i ? null : i);

  const exportCSV = (list) => {
    const header = [
      "nom", "adresse", "secteur", "resume", "site_web",
      "contact_poste", "contact_nom", "contact_email",
      "contact_tel", "contact_linkedin"
    ];
    const rows = [];
    list.forEach((ent) => {
      const contacts = ent.contacts?.length
        ? ent.contacts
        : [{ poste: "non disponible", nom: "non disponible", email: "", telephone: "", linkedin: "" }];
      contacts.slice(0, 3).forEach((c) => {
        rows.push([
          ent.nom || "",
          ent.adresse || "",
          ent.secteur || "",
          (ent.resume || "").replace(/[\r\n]+/g, " "),
          ent.site_web || "",
          c.poste || "",
          c.nom || "",
          c.email || "",
          c.telephone || "",
          c.linkedin || "",
        ]);
      });
    });
    const csv = [header, ...rows].map(r => r.map(v => `"${v}"`).join(",")).join("\r\n");
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "prospects.csv";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      {/* Recherche et export */}
      <div className="flex flex-col sm:flex-row gap-2 mb-6">
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Rechercher nom / secteur / résumé"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={() => exportCSV(filtered)}
          className="px-4 py-2 bg-indigo-600 text-white rounded"
        >
          Export CSV
        </button>
      </div>

      {/* Liste */}
      <div className="space-y-4">
        {filtered.map((ent, i) => (
          <div key={i} className="border p-4 rounded bg-white shadow-sm">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-lg font-semibold">{ent.nom}</h2>
                <p className="text-sm text-gray-500">{ent.secteur} • {ent.adresse}</p>
              </div>
              <div className="flex gap-2">
                {ent.site_web && <a href={ent.site_web} target="_blank" rel="noopener" className="underline text-indigo-600">Site</a>}
                <button onClick={() => toggle(i)} className="px-2 py-1 border rounded text-sm">
                  {openIdx === i ? "Réduire" : "Détails"}
                </button>
              </div>
            </div>

            {openIdx === i && (
              <div className="mt-4 space-y-2 text-sm text-gray-700">
                <p>{ent.resume}</p>
                {ent.contacts?.length ? (
                  <div className="space-y-2">
                    {ent.contacts.map((c, ci) => (
                      <div key={ci} className="p-2 border rounded bg-gray-50">
                        <div><strong>{c.poste}</strong> — {c.nom}</div>
                        <div>Email: {c.email || "non disponible"} • Tel: {c.telephone || "non disponible"}</div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div>Contacts non disponibles</div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
