// src/App.jsx
import React, { useState } from "react";
import CompanyProspectList from "./companyprospect";

export default function App() {
  const [companyName, setCompanyName] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSearch() {
    if (!companyName || !postalCode) {
      alert("Veuillez entrer le nom et le code postal de l'entreprise.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://localhost:5000/prospect", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ companyName, location: postalCode }),
      });

      if (!response.ok) {
        throw new Error("Erreur lors de la recherche");
      }

      const result = await response.json();
      console.log("üì¶ Donn√©es re√ßues :", result);

      // üß† On adapte les donn√©es de Pappers au format attendu par CompanyProspectList
      const formatted = (result.pappersResults || result.resultats || []).map((e) => ({
        nom: e.nom_entreprise || e.denomination || "Nom inconnu",
        adresse: e.siege
          ? `${e.siege.adresse_ligne_1 || ""}, ${e.siege.code_postal || ""} ${e.siege.ville || ""}`
          : "Adresse non disponible",
        secteur: e.libelle_code_naf || e.domaine_activite || "Secteur non renseign√©",
        resume: `Entreprise ${e.forme_juridique || "inconnue"}, cr√©√©e le ${
          e.date_creation_formate || "?"}, effectif : ${
          e.effectif || "?"} salari√©s, chiffre d‚Äôaffaires : ${
          e.chiffre_affaires?.toLocaleString?.() || "?"} ‚Ç¨.`,
        site_web: e.site_web || "",
        contacts: [],
        sources: ["Pappers"],
      }));
      console.log(" Donn√©es format√©es pour affichage", formatted)
      setData(formatted);
    } catch (err) {
      console.error(err);
      setError("Impossible de r√©cup√©rer les donn√©es. V√©rifiez le backend.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Prospection Entreprises</h1>

      {/* Formulaire de recherche */}
      <div className="flex gap-2 mb-4">
        <input
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          placeholder="Nom de l'entreprise"
          className="flex-1 p-2 border rounded"
        />
        <input
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          placeholder="Code postal"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={handleSearch}
          className="px-3 py-2 bg-slate-700 text-white rounded"
        >
          {loading ? "Chargement..." : "Rechercher"}
        </button>
      </div>

      {/* Messages */}
      {error && <p className="text-red-600">{error}</p>}
      {loading && <p>Recherche en cours...</p>}

      {/* R√©sultats */}
      {data.length > 0 && <CompanyProspectList data={data} />}
    </div>
  );
}
