import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix des ic√¥nes Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

export default function MapProspects({ onSearch }) {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markersRef = useRef([]);
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [companyName, setCompanyName] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [center, setCenter] = useState({ lat: 48.8566, lng: 2.3522 });
  const [filters, setFilters] = useState({
    radius: 100,
    sector: '',
    hasEmail: false,
    hasPhone: false
  });
  const [favorites, setFavorites] = useState(() => {
    return new Set(JSON.parse(localStorage.getItem('favorites') || '[]'));
  });
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Fonction de recherche
  // Fonction de recherche
const handleSearch = async (e) => {
  if (e) e.preventDefault();

  if (!companyName || !postalCode) {
    setError('Nom et code postal requis');
    return;
  }

  setLoading(true);
  setError(null);

  try {
    const res = await fetch('http://localhost:5000/prospect', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        companyName,
        postalCode,          // <-- ICI, pas "location"
        radiusKm: filters.radius || 100,
      }),
    });
    // ... le reste de ton code identique


    const text = await res.text();
    let js = null;
    try {
      js = text ? JSON.parse(text) : null;
    } catch (parseErr) {
      setError(`R√©ponse inattendue du serveur (status ${res.status})`);
      setLoading(false);
      return;
    }

    if (!res.ok) {
      setError(js?.error || `Erreur serveur (${res.status})`);
      setLoading(false);
      return;
    }

    const companies = (js.data || []).map((c, i) => ({
      id: i,
      name: c.nom || c.nom_entreprise || "Inconnu",
      address: c.adresse || c.adresse_siege || "",
      sector: c.secteur || c.libelle_activite_principale || c.libelle_naf || "",
      site: c.site_web || c.site || "",
      telephone: c.telephone || "",
      contacts: c.contacts || [],
      latitude: c.latitude || c.lat || null,
      longitude: c.longitude || c.lng || null,
      ville: c.ville || "",
      code_postal: c.code_postal || "",
      adresse: c.adresse || "",
      naf: c.naf || c.activite_principale || "",
      siren: c.siren || `temp-${i}`,
    }));

    setData(companies);

    // Centre la carte
    // Centre la carte sur le code postal saisi
    const centerFromCp = await geocodePostal(postalCode);
    if (centerFromCp && mapInstance.current) {
    mapInstance.current.setView([centerFromCp.lat, centerFromCp.lng], 11);
    setCenter(centerFromCp);
    }

  } catch (err) {
    setError(err.message || 'Erreur r√©seau');
  } finally {
    setLoading(false);
  }
};

const geocodePostal = async (cp) => {
  const res = await fetch(
    `https://api-adresse.data.gouv.fr/search/?q=${encodeURIComponent(cp)}&limit=1`
  );
  const json = await res.json();
  const feat = json.features?.[0];
  if (!feat) return null;
  const [lon, lat] = feat.geometry.coordinates;
  return { lat, lng: lon };
};


  // Initialise la carte Leaflet
  useEffect(() => {
    if (!mapRef.current || mapInstance.current) return;

    mapInstance.current = L.map(mapRef.current).setView([center.lat, center.lng], 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '¬© OpenStreetMap contributors',
      maxZoom: 19
    }).addTo(mapInstance.current);

    console.log('‚úÖ Carte Leaflet initialis√©e');
  }, []);

  // Cr√©e les markers
  useEffect(() => {
    if (!mapInstance.current) return;

    // Nettoie anciens markers
    markersRef.current.forEach(m => mapInstance.current.removeLayer(m));
    markersRef.current = [];

    if (filteredData.length === 0) return;

    const bounds = [];
    const seenAddresses = new Map();

    filteredData.forEach(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng || isNaN(lat) || isNaN(lng)) return;

      // D√©duplication
      const addressKey = `${lat.toFixed(4)},${lng.toFixed(4)}`;
      if (seenAddresses.has(addressKey)) return;
      seenAddresses.set(addressKey, true);

      // Ic√¥ne personnalis√©e
      const hasContacts = company.contacts && company.contacts.length > 0;
      const isFavorite = favorites.has(company.siren);

      const iconColor = isFavorite ? '#f4b400' : (hasContacts ? '#0f9d58' : '#db4437');
      const iconSize = isFavorite ? 32 : 24;

      const customIcon = L.divIcon({
        className: 'custom-leaflet-marker',
        html: `<div style="
          background: ${iconColor};
          width: ${iconSize}px;
          height: ${iconSize}px;
          border-radius: 50%;
          border: 3px solid white;
          box-shadow: 0 2px 8px rgba(0,0,0,0.3);
        "></div>`,
        iconSize: [iconSize, iconSize],
        iconAnchor: [iconSize/2, iconSize/2]
      });

      const marker = L.marker([lat, lng], { icon: customIcon })
        .addTo(mapInstance.current)
        .bindPopup(() => createPopupContent(company))
        .on('click', () => setSelectedCompany(company.siren));

      markersRef.current.push(marker);
      bounds.push([lat, lng]);
    });

    // Ajuste le zoom
    if (bounds.length > 0) {
      mapInstance.current.fitBounds(bounds, { padding: [50, 50] });
    }
  }, [filteredData, favorites]);

  // Popup personnalis√©e
  const createPopupContent = (company) => {
    const contactsHtml = company.contacts && company.contacts.length > 0
      ? `
        <div style="margin-top: 12px;">
          <strong>üë• Contacts (${company.contacts.length})</strong>
          ${company.contacts.map(c => `
            <div style="padding: 8px; background: #f8f9fa; border-radius: 6px; margin-top: 6px;">
              <div style="font-weight: 600;">${c.nom || 'Nom inconnu'}</div>
              <div style="font-size: 12px; color: #666;">${c.poste || ''}</div>
              ${c.email ? `<div style="font-size: 12px;">‚úâÔ∏è ${c.email}</div>` : ''}
              ${c.telephone ? `<div style="font-size: 12px;">üìû ${c.telephone}</div>` : ''}
              ${c.linkedin ? `<div style="font-size: 12px;"><a href="${c.linkedin}" target="_blank">üîó LinkedIn</a></div>` : ''}
            </div>
          `).join('')}
        </div>
      `
      : '<div style="color: #999; margin-top: 12px;">Aucun contact disponible</div>';

    return `
      <div style="max-width: 350px; padding: 12px;">
        <h3 style="margin: 0 0 12px 0; padding-bottom: 8px; border-bottom: 2px solid #1a73e8;">
          ${company.name || company.nom || 'Entreprise'}
        </h3>
        <p style="margin: 8px 0;">
          <strong>üìç Adresse</strong><br>
          ${company.address || company.adresse || ''}<br>
          ${company.code_postal || ''} ${company.ville || ''}
        </p>
        <p style="margin: 8px 0;">
          <strong>üè¢ Secteur</strong><br>
          ${company.sector || company.secteur || 'Non renseign√©'}<br>
          <span style="color: #999; font-size: 12px;">NAF: ${company.naf || 'N/A'}</span>
        </p>
        ${company.site || company.site_web ? `
          <p style="margin: 8px 0;">
            <strong>üåê Site web</strong><br>
            <a href="${company.site || company.site_web}" target="_blank">${company.site || company.site_web}</a>
          </p>
        ` : ''}
        ${company.telephone ? `
          <p style="margin: 8px 0;">
            <strong>üìû T√©l√©phone</strong><br>
            <a href="tel:${company.telephone}">${company.telephone}</a>
          </p>
        ` : ''}
        ${contactsHtml}
      </div>
    `;
  };  // Applique les filtres
  const applyFilters = () => {
    // Si pas de donn√©es, pas de filtrage
    if (data.length === 0) {
      setFilteredData([]);
      return;
    }

    let filtered = data.filter(company => {
      const lat = parseFloat(company.latitude);
      const lng = parseFloat(company.longitude);

      if (!lat || !lng) return false;

      // Filtre secteur
      if (filters.sector && company.naf !== filters.sector) return false;

      // Filtre email
      if (filters.hasEmail && (!company.contacts || !company.contacts.some(c => c.email))) {
        return false;
      }

      // Filtre t√©l√©phone
      if (filters.hasPhone && !company.telephone && (!company.contacts || !company.contacts.some(c => c.telephone))) {
        return false;
      }

      return true;
    });

    setFilteredData(filtered);
  };

  useEffect(() => {
    applyFilters();
  }, [filters, data, center]);

  // Calcul distance (Haversine)
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Toggle favoris
  const toggleFavorite = (siren) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(siren)) {
      newFavorites.delete(siren);
    } else {
      newFavorites.add(siren);
    }
    setFavorites(newFavorites);
    localStorage.setItem('favorites', JSON.stringify([...newFavorites]));
  };

  const sectors = [...new Set(data.map(c => c.naf).filter(Boolean))];

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{ 
        width: '380px', 
        background: 'white', 
        borderRight: '1px solid #ddd', 
        overflowY: 'auto', 
        padding: '20px',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <h2 style={{ marginBottom: '16px', fontSize: '20px' }}>üéØ Prospection</h2>

        {/* Formulaire de recherche */}
        <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
          <div style={{ marginBottom: '12px' }}>
            <label style={{ display: 'block', marginBottom: '6px', fontWeight: '500', fontSize: '13px' }}>
              Nom entreprise
            </label>
            <input
              type="text"
              placeholder="Ex: Castorama"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '10px', 
                borderRadius: '8px', 
                border: '1px solid #ddd',
                fontSize: '14px'
              }}
            />
          </div>

          <div style={{ marginBottom: '12px' }}>
            <label style={{ display: 'block', marginBottom: '6px', fontWeight: '500', fontSize: '13px' }}>
              Code postal
            </label>
            <input
              type="text"
              placeholder="Ex: 75015"
              value={postalCode}
              onChange={(e) => setPostalCode(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '10px', 
                borderRadius: '8px', 
                border: '1px solid #ddd',
                fontSize: '14px'
              }}
            />
          </div>

          <button 
            type="submit" 
            disabled={loading}
            style={{ 
              width: '100%', 
              padding: '12px', 
              background: loading ? '#ccc' : '#1a73e8',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '15px',
              fontWeight: '600',
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'background 0.2s'
            }}
          >
            {loading ? 'üîÑ Recherche...' : 'üîç Rechercher'}
          </button>
        </form>

        {error && (
          <div style={{ 
            padding: '12px', 
            background: '#fee', 
            color: '#c00', 
            borderRadius: '8px', 
            marginBottom: '16px',
            fontSize: '13px'
          }}>
            ‚ùå {error}
          </div>
        )}

        {data.length === 0 && !loading && (
          <div style={{ 
            padding: '20px', 
            textAlign: 'center', 
            color: '#666',
            fontSize: '14px'
          }}>
            üìç Effectuez une recherche pour afficher des entreprises
          </div>
        )}
        <div style={{ marginBottom: '20px' }}>
          <span style={{ background: '#e8f0fe', padding: '6px 12px', borderRadius: '16px', marginRight: '8px', fontSize: '13px' }}>
            {data.length} entreprises
          </span>
          <span style={{ background: '#e8f0fe', padding: '6px 12px', borderRadius: '16px', fontSize: '13px' }}>
            {filteredData.length} visibles
          </span>
        </div>

        {/* Filtres */}
        {data.length > 0 && (
          <>
            <div style={{ 
              padding: '12px', 
              background: '#f0f9ff', 
              borderRadius: '8px', 
              marginBottom: '16px',
              fontSize: '13px',
              color: '#1e3a8a'
            }}>
              üìç Recherche autour de: {postalCode || 'Non d√©fini'}
            </div>
          </>
        )}

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', fontSize: '13px' }}>üè¢ Secteur NAF</label>
          <select
            value={filters.sector}
            onChange={(e) => setFilters({ ...filters, sector: e.target.value })}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }}
          >
            <option value="">Tous les secteurs</option>
            {sectors.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'flex', alignItems: 'center', marginBottom: '8px', fontSize: '14px' }}>
            <input
              type="checkbox"
              checked={filters.hasEmail}
              onChange={(e) => setFilters({ ...filters, hasEmail: e.target.checked })}
              style={{ marginRight: '8px' }}
            />
            Avec Email
          </label>
          <label style={{ display: 'flex', alignItems: 'center', fontSize: '14px' }}>
            <input
              type="checkbox"
              checked={filters.hasPhone}
              onChange={(e) => setFilters({ ...filters, hasPhone: e.target.checked })}
              style={{ marginRight: '8px' }}
            />
            Avec T√©l√©phone
          </label>
        </div>

        {/* Liste */}
        <div style={{ flex: 1, overflowY: 'auto' }}>
          {filteredData.map((company, i) => {
            const isFav = favorites.has(company.siren);
            const isSelected = selectedCompany === company.siren;
            return (
              <div
                key={i}
                style={{
                  background: isSelected ? '#e8f0fe' : 'white',
                  border: `1px solid ${isSelected ? '#1a73e8' : '#ddd'}`,
                  borderRadius: '12px',
                  padding: '16px',
                  marginBottom: '12px',
                  cursor: 'pointer',
                  transition: 'all 0.2s'
                }}
                onClick={() => {
                  setSelectedCompany(company.siren);
                  const lat = parseFloat(company.latitude);
                  const lng = parseFloat(company.longitude);
                  if (lat && lng && mapInstance.current) {
                    mapInstance.current.setView([lat, lng], 14);
                    const marker = markersRef.current.find(m => {
                      const pos = m.getLatLng();
                      return Math.abs(pos.lat - lat) < 0.0001 && Math.abs(pos.lng - lng) < 0.0001;
                    });
                    if (marker) marker.openPopup();
                  }
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                  <strong style={{ fontSize: '15px' }}>{company.name || company.nom}</strong>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleFavorite(company.siren);
                    }}
                    style={{ 
                      background: 'none', 
                      border: 'none', 
                      cursor: 'pointer', 
                      fontSize: '20px',
                      padding: '0 4px'
                    }}
                  >
                    {isFav ? '‚≠ê' : '‚òÜ'}
                  </button>
                </div>
                <div style={{ fontSize: '13px', color: '#666' }}>
                  üìç {company.ville}<br />
                  üè¢ {(company.sector || company.secteur || 'Secteur non renseign√©').substring(0, 50)}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Carte */}
      <div ref={mapRef} style={{ flex: 1 }} />
    </div>
  );
}
