// src/App.jsx
import React, { useState } from "react";
import CompanyProspectList from "./companyprospect";

export default function App() {
  const [companyName, setCompanyName] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSearch() {
    if (!companyName || !postalCode) {
      alert("Veuillez entrer le nom et le code postal de l'entreprise.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://localhost:5000/prospect", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ companyName, location: postalCode }),
      });

      if (!response.ok) throw new Error("Erreur lors de la recherche");

      const result = await response.json();
      console.log("üì¶ Donn√©es re√ßues :", result);

      // üß† Le backend renvoie une longue cha√Æne de texte dans result.result
      const text = result.result || "";
      const regex =
        /\d+\.\s*Entreprise\s*:\s*(.+?)\n\s*-\s*Secteur\s*:\s*(.+?)\n\s*-\s*Localisation\s*:\s*(.+?)\n\s*-\s*Code postal\s*:\s*(.+?)\n\s*-\s*Nombre d'employ√©s\s*:\s*(.+?)\n+([\s\S]*?)(?=\n\d+\. Entreprise|$)/g;

      const entreprises = [];
      let match;
      while ((match = regex.exec(text)) !== null) {
        const [_, nom, secteur, adresse, codePostal, effectif, contactsBlock] = match;

        // Extraire les contacts dans le bloc correspondant
        const contactRegex =
          /-\s*(Directeur G√©n√©ral|Responsable [^:]+)\s*:\s*([^\n]+)\n\s*- Email\s*:\s*([^\n]+)\n\s*- T√©l√©phone\s*:\s*([^\n]+)\n\s*- LinkedIn\s*:\s*([^\n]+)/g;

        const contacts = [];
        let c;
        while ((c = contactRegex.exec(contactsBlock)) !== null) {
          contacts.push({
            poste: c[1],
            nom: c[2],
            email: c[3] === "non disponible" ? "" : c[3],
            telephone: c[4] === "non disponible" ? "" : c[4],
            linkedin: c[5].includes("non disponible") ? "" : `https://linkedin.com/in/${c[5].trim()}`,
          });
        }

        entreprises.push({
          nom: nom.trim(),
          adresse: `${adresse.trim()} (${codePostal.trim()})`,
          secteur: secteur.trim(),
          resume: `Effectif : ${effectif.trim()}`,
          contacts,
          site_web: "",
          sources: ["Groq AI"],
        });
      }

      console.log("‚úÖ Entreprises extraites :", entreprises);
      setData(entreprises);
    } catch (err) {
      console.error(err);
      setError("Impossible de r√©cup√©rer les donn√©es. V√©rifiez le backend.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Prospection Entreprises</h1>

      <div className="flex gap-2 mb-4">
        <input
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          placeholder="Nom de l'entreprise"
          className="flex-1 p-2 border rounded"
        />
        <input
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          placeholder="Code postal"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={handleSearch}
          className="px-3 py-2 bg-slate-700 text-white rounded"
        >
          {loading ? "Chargement..." : "Rechercher"}
        </button>
      </div>

      {error && <p className="text-red-600">{error}</p>}
      {loading && <p>Recherche en cours...</p>}

      {data.length > 0 && <CompanyProspectList data={data} />}
    </div>
  );
}
