import React, { useState, useMemo } from "react";

export default function CompanyProspectList({ data }) {
  const [query, setQuery] = useState("");
  const [openIdx, setOpenIdx] = useState(null);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return data;
    return data.filter(
      (e) =>
        (e.nom || "").toLowerCase().includes(q) ||
        (e.secteur || "").toLowerCase().includes(q) ||
        (e.resume || "").toLowerCase().includes(q)
    );
  }, [data, query]);

  const toggle = (i) => setOpenIdx(openIdx === i ? null : i);

  const exportCSV = (list) => {
    const header = ["nom", "adresse", "secteur", "resume", "site_web", "contact_poste", "contact_nom", "contact_email", "contact_tel", "contact_linkedin", "sources"];
    const rows = [];
    list.forEach((ent) => {
      const contacts = ent.contacts?.length ? ent.contacts : [{ poste: "non disponible", nom: "non disponible", email: "", telephone: "", linkedin: "" }];
      contacts.slice(0, 3).forEach((c) => {
        rows.push([
          ent.nom || "",
          ent.adresse || "",
          ent.secteur || "",
          (ent.resume || "").replace(/[\r\n]+/g, " "),
          ent.site_web || "",
          c.poste || "",
          c.nom || "",
          c.email || "",
          c.telephone || "",
          c.linkedin || "",
          Array.isArray(ent.sources) ? ent.sources.join("; ") : ent.sources || "",
        ]);
      });
    });
    const csv = [header, ...rows].map(r => r.map(v => `"${v}"`).join(",")).join("\r\n");
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `prospects-${new Date().toISOString().slice(0,10)}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="flex flex-col sm:flex-row gap-4 mb-8">
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="üîé Rechercher (nom / secteur / r√©sum√©)"
          className="flex-1 p-4 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-indigo-500"
        />
        <button
          onClick={() => exportCSV(filtered)}
          className="px-8 py-4 bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold rounded-xl hover:from-green-600 shadow-lg transition-all"
        >
          üì• Export CSV ({filtered.length})
        </button>
      </div>

      <div className="grid gap-6">
        {filtered.map((ent, i) => (
          <div key={i} className="bg-white border-2 border-gray-100 rounded-2xl p-8 shadow-xl hover:shadow-2xl transition-all hover:-translate-y-1">
            <div className="flex justify-between items-start mb-6">
              <div className="flex-1">
                <h2 className="text-2xl font-bold text-gray-800 mb-1">{ent.nom}</h2>
                <div className="flex flex-wrap gap-2 text-sm">
                  <span className="px-3 py-1 bg-indigo-100 text-indigo-800 rounded-full">üìä {ent.secteur}</span>
                  <span className="px-3 py-1 bg-gray-100 text-gray-700 rounded-full">üìç {ent.adresse}</span>
                </div>
              </div>
              <div className="flex gap-3 ml-4">
                {ent.site_web && (
                  <a href={ent.site_web} target="_blank" rel="noopener" className="px-4 py-2 bg-blue-500 text-white rounded-xl hover:bg-blue-600 transition-all">
                    üåê Site
                  </a>
                )}
                <button
                  onClick={() => toggle(i)}
                  className="px-6 py-2 border-2 border-indigo-500 text-indigo-600 font-semibold rounded-xl hover:bg-indigo-50 transition-all"
                >
                  {openIdx === i ? "‚àí R√©duire" : "+ D√©tails"}
                </button>
              </div>
            </div>

            {openIdx === i && (
              <div className="space-y-6">
                <div>
                  <h3 className="font-semibold text-gray-700 mb-2">üìù R√©sum√©</h3>
                  <p className="text-gray-600 leading-relaxed">{ent.resume}</p>
                </div>

                <div>
                  <h3 className="font-semibold text-gray-700 mb-3">üë• Contacts prioritaires</h3>
                  {ent.contacts.length ? (
                    <div className="space-y-3">
                      {ent.contacts.map((c, ci) => (
                        <div key={ci} className="p-4 bg-gradient-to-r from-indigo-50 to-blue-50 border rounded-xl">
                          <div className="font-semibold text-lg mb-1">{c.nom}</div>
                          <div className="text-sm text-gray-600 mb-2">{c.poste}</div>
                          <div className="flex flex-wrap gap-4 text-sm">
                            {c.email && <span className="bg-white px-3 py-1 rounded-full border shadow-sm">üìß {c.email}</span>}
                            {c.telephone && <span className="bg-white px-3 py-1 rounded-full border shadow-sm">üìû {c.telephone}</span>}
                            {c.linkedin && <a href={c.linkedin} target="_blank" className="bg-white px-3 py-1 rounded-full border shadow-sm underline">üíº LinkedIn</a>}
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-gray-500 italic">Contacts en cours de recherche...</p>
                  )}
                </div>

                <div className="p-4 bg-green-50 border-2 border-green-200 rounded-xl">
                  <h4 className="font-semibold text-green-800 mb-2">üìö Sources v√©rifi√©es</h4>
                  <div className="text-sm text-green-700">
                    {Array.isArray(ent.sources) 
                      ? ent.sources.map((src, si) => <span key={si} className="inline-block bg-white px-2 py-1 rounded mr-2 mb-1 border">{src}</span>)
                      : ent.sources
                    }
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {filtered.length === 0 && query && (
        <div className="text-center py-12 text-gray-500">
          <p className="text-2xl">üòî Aucun r√©sultat pour "{query}"</p>
        </div>
      )}
    </div>
  );
}
