import React, { useState } from "react";
import CompanyProspectList from "/home/dehlia/prospection/tableau-entreprises/frontend/src/CompanyProspect";

export default function App() {
  const [data, setData] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);

  const searchCompanies = async () => {
    if (!query.trim()) return;
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `Recherche les entreprises autour de ${query} avec infos DG, RSE, Supply Chain...`
        })
      });
      const json = await res.json();
      setData(json); // json doit être un tableau compatible CompanyProspectList
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Recherche dynamique d'entreprises</h1>

      <div className="flex gap-2 mb-4">
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Nom de l'entreprise + code postal"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={searchCompanies}
          className="px-3 py-2 bg-indigo-600 text-white rounded"
        >
          {loading ? "Recherche..." : "Rechercher"}
        </button>
      </div>

      <CompanyProspectList data={data} />
    </div>
  );
}
